<?php
require_once './vendor/autoload.php';
use Dompdf\Dompdf;

$html='
<html>
<head>
<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
<title>Pedazo de PDF</title>
</head>
<body>

<h2>Ingredientes para aprobar DWES</h2>
<p>Ingredientes:</p>
<dl>
<dd>Perseverancia</dd>
<dd>Constancia</dd>
<dd>Optimismo</dd>
<dd>Autoestima</dd>
<dd>Trabajo en Equipo</dd>
<dd>Jam√≥n Pata Negra</dd>
<img src="data:image/png;base64,"/>
</dl>
</body>
</html>';
$mipdf = new Dompdf();
# Definimos el tama√±o y orientaci√≥n del papel que queremos.
# O por defecto coger√° el que est√° en el fichero de configuraci√≥n.
$mipdf ->set_paper("A4", "portrait");
# Cargamos el contenido HTML.
$mipdf ->load_html($html); //aqui puede entrar un .html

# Renderizamos el documento PDF.
$mipdf ->render();

# Creamos un fichero
$pdf = $mipdf->output();
$filename = "HeavenTicket.pdf";
file_put_contents($filename, $pdf);

# Enviamos el fichero PDF al navegador.

$salida = $mipdf->stream($filename,array('Attachment'=>0));
header("Location: $salida");
?>